import React, { useState } from "react";
import './ExpenseForm.css'

export default function ExpenseForm() {

  // const [enteredTitle, setEnteredTitle] = useState('');
  // const [enteredAmount, setEnterAmount] = useState('');
  // const [enteredDate, setEnterDate] = useState('');

  const [userInput, setUserInput] = useState({
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: ''
  });

  const titleChangeHandler = function(event) {
    setUserInput({
      ...userInput,
      enteredTitle: event.target.value
    })
  };

  const amountChangeHandler = function(event) {
    setUserInput({
      ...userInput,
      enteredAmount: event.target.value
    });
  };

  const dateChangeHandler = function(event) {
    setUserInput({
      ...userInput,
      enteredDate: event.target.value
    })
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense_control">
          <label>Name of keyboard</label>
          <input type='text' onChange={titleChangeHandler}/>
        </div>
        <div className="new-expense_control">
          <label>Amount</label>
          <input type='number' min="1" step="1" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense_control">
          <label>Date</label>
          <input type='date' min="2022-01-01" max="2024-12-31" onChange={dateChangeHandler}/>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Add keyboard</button>
        </div>
      </div>
    </form>
  )

}
