import React, { useState } from "react";
import './ExpenseForm.css'

export default function ExpenseForm(props) {

  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState('');
  const [enteredDate, setEnteredDate] = useState('');

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: ''
  // });

  const titleChangeHandler = function(event) {
    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.target.value
    // })
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = function(event) {
    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.target.value
    // });
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = function(event) {
    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.target.value
    // })
    setEnteredDate(event.target.value);
  };

  const submitHandler = function(event) {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    }
    props.onSaveExpenseData(expenseData);
    setEnteredTitle('');
    setEnteredAmount('');
    setEnteredDate('');
  }

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense_control">
          <label>Name of keyboard</label>
          <input type='text' 
          value={enteredTitle}
          onChange={titleChangeHandler}/>
        </div>
        <div className="new-expense_control">
          <label>Amount</label>
          <input type='number' value={enteredAmount} min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense_control">
          <label>Date</label>
          <input type='date' value={enteredDate} min="1990-01-01" max="2030-12-31" onChange={dateChangeHandler}/>
        </div>
        <div className="new-expense__actions">
          <button type="button" onClick={props.onCancel}>Cancel</button>
          <button type="submit">Add keyboard</button>
        </div>
      </div>
    </form>
  )

}
